mapper.update:
UPDATE project_freeboard
SET hit = hit+1
WHERE no=#{no}

mapper.select:
SELECT no, name, subject, content, TO_CHAR(regdate, 'YYYY-MM-DD') as dbday, hit
FROM project_freeboard
WHERE no=#{no}

mapper.update:
UPDATE project_freeboard
SET name=#{name}, subject=#{subject}, content=#{content}
WHERE no=#{no}
